        userTypesList: [],
        academicProgramsList: [],
        documentTypesList: [],
        UserType: '',
        Name: '',
        LastName: '',
        Identifier: '',
        Email: '',
        DocumentType: '',
        DocumentNumber: '',
        AcademicProgram: '',
        Password: '',
        Photo: null,
        TermAndConditions: true,
        status: false
    }


