import React, { Component } from 'react';
import Principal from './Principal';
import fondo from '../img/fondo-inicio.png';
import logo from '../img/logo.png';
import '../css/Perfilcss.css'
import axios from 'axios';
import url from '../Conexions/conexion.js'

const baseURL2 = url.url + "/Profile";

class Perfil extends Component {
    state = {
        userType: [],
        profile: [],
        photo: '',
        name_LastName: '',
        id: '',
        identifier: '',
        email: '',
        academicProgram: '',
        userType: '',
        status: false

    }

    componentDidMount = () => {
        this.getperfil();
    }

    getperfil() {
        const config2 = {
            headers: { 'Authorization': 'Bearer ' + localStorage.getItem("token") }
        }

        axios.get(baseURL2, config2).then(res => {
            this.setState({
                profile: res.data.profile,
                photo: res.data.profile.photo,
                name_LastName: res.data.profile.name_LastName,
                id: res.data.profile.id,
                email: res.data.profile.email,
                identifier: res.data.profile.identifier,
                academicProgram: res.data.profile.academicProgram,
                userType: res.data.userType,
                status: true
            })
            // console.log(res.data.profile.photo)
        }).catch((error) => {
            console.log(error);
        });


    }
    render() {
        return (
            <section className='vh-100 img-fluid' style={{ backgroundImage: `url(${fondo})`, backgroundSize: '100% 100%' }}>
                <div className='sb-nav-fixed'>
                    <div className='row g-0'>
                        {/* <Principal /> */}
                        <div id="layoutSidenav_content">

                            <div className='container-fluid px-4' style={{ height: '100%' }}>
                                <div className='container py-5 h-100'>
                                    <div className='row d-flex justify-content-center align-items-center h-100'>
                                        <div className='card col-md-10 col-lg-3' style={{ borderRadius: '1rem' }}>
                                            <div className='row g-0'>
                                                <div className='card-body p-4 p-lg-5 text-black'>

                                                    {/* <div className='margen-top h-100'>
                                                        <div className='d-flex justify-content-center align-items-center h-100'>
                                                            <div className=''>
                                                                <div className='card' style={{ borderRadius: '3rem' }}>
                                                                    <div className=''>
                                                                        <div className='align-items-center'>
                                                                            <div className=' p-4 text-black'> */}
                                                                                <form className="form">
                                                                                <section className="centrarelementos">
                                                                                    <img className="logo" src={logo} ></img>
                                                                                    <h2 className="text-center mb-4">PERFIL</h2>
                                                                                </section>
                                                                                    <img className='img-perfil' src={this.state.photo}></img>
                                                                                    <label className="text-center mb-4">-----------------------------------------------------------</label>

                                                                                    {/* nombre */}
                                                                                    <label className="text-center mb-4">{this.state.name_LastName}</label>
                                                                                    {/* id institucional*/}
                                                                                    <label className="text-center mb-4">ID: {this.state.identifier}</label>
                                                                                    {/* correo*/}
                                                                                    <label className="text-center mb-4">{this.state.email}</label>
                                                                                    {/*Carrera*/}
                                                                                    <label className="text-center mb-4">{this.state.academicProgram}</label>

                                                                                    <div className="mb-4 centrarelementos">
                                                                                        <a className="link" href="/PerfilUpdate"><button type="button" className="btn btn-outline-danger form-label boton-color" >MODIFICAR</button></a>                                                            </div>
                                                                                </form>
                                                                            </div>
                                                                        </div>
                                                                    </div>
                                                                </div>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        {/* </div>
                                    </div>
                                </div>
                            </div>
                        </div>

                    </div>
                </div> */}
            </section>
        );
    }
}

export default Perfil;
